import React, { useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import '../../css/home.css';
import '../../css/colors.css';

const Home = (props) => {

  useEffect(() => {
    props.setShowFooter(true);
    document.title = "SHAREHOME - A custom party game";
  });

  return (
    <div className="content">
      <NavLink to="/"><div className="title noselect"></div></NavLink>
      <p className="subtitle">
        A custom party game inspired by Terrace House
      </p>
      <div className="CTAWrapper">
        <a className="callToAction noselect" href="/play">
          <h1>Play virtually right now</h1>
          <p>(video conferencing not included)</p>
        </a>
        <a className="callToAction noselect" href="/purchase">
          <h1>Purchase a physical copy</h1>
          <p>(for when quarantine is finally over)</p>
        </a>
        <a className="callToAction noselect" href="/print">
          <h1>Print a shitty paper version</h1>
          <p>(free if you somehow have a printer)</p>
        </a>
      </div>
    </div>
  );
}

export default Home;
