import React from 'react';
import '../../css/cardButtons.css';

function CardButtons(props){
  let currentCard = props.currentCard;

  return (
    <div className="contentButtonWrapper contentWrapper">
      <input
        id="imageFileInput"
        type="file"
        onChange={props.handleInputChange}
        name="image"
        accept="image/*"
      />
      <label id="imageFileLabel" class="contentButton" htmlFor="imageFileInput">Change Image</label>
      <button className="contentButton" onClick={props.duplicateCurrentCard}>Duplicate Card</button>
      <button className="contentButton" onClick={props.removeCurrentCard}>Delete Card</button>
    </div>
  )
}

export default CardButtons;
