import React from 'react';
import { PDFViewer } from '@react-pdf/renderer';

//custom files
import PDFDocument from './PDFDocument';
import InputWrapper from './InputWrapper';
import downloadPDFFile from './downloadPDFFile.js';
import * as CardEditorConstants from './CardEditorConstants.js';

//wrapper for card editor section (including input + PDF)
function CardEditorWrapper(props){
  const currentCard = props.currentCard;
  const setCurrentCard = props.setCurrentCard;

  //PDF document
  const cardDocument = (
    <PDFDocument
      name={currentCard.name}
      japaneseName={currentCard.japaneseName}
      age={currentCard.age}
      job={currentCard.job}
      image={currentCard.image}
    />
  )

  //returns a new card details object with newValue for newProp
  const updateCurrentCard = (newProp, newValue) => {
    return {
      ...currentCard,
      [newProp]: newValue
    }
  }

  //event handlers for inputs
  const handleNameChange = (e) => {
    console.log(e.target);
    setCurrentCard(updateCurrentCard(e.target.name, e.target.value));
  }
  const handleJapaneseNameChange = (e) => {
    setCurrentCard(updateCurrentCard("japaneseName", e.target.value));
  }
  const handleAgeChange = (e) => {
    setCurrentCard(updateCurrentCard("age", e.target.value));
  }
  const handleJobChange = (e) => {
    setCurrentCard(updateCurrentCard("job", e.target.value));
  }
  const handleImageChange = (e) => {
    const tempImage = (e.target.files[0]) ? URL.createObjectURL(e.target.files[0]) : CardEditorConstants.defaultImage;
    setCurrentCard(updateCurrentCard("image", tempImage));
  }
  const handleDownload = (e) => {
    downloadFile(cardDocument, currentCard.name + ".pdf");
  }

  return (
    <div>
      <div className="PDFWrapper" style={{
        height: CardEditorConstants.PDFDimensions.height * 2,
        width: CardEditorConstants.PDFDimensions.width * 2
      }}>
        <PDFViewer scrolling="no" className="PDFViewer">
          {cardDocument}
        </PDFViewer>
      </div>
      <InputWrapper
        handleNameChange={handleNameChange}
        handleJapaneseNameChange={handleJapaneseNameChange}
        handleAgeChange={handleAgeChange}
        handleJobChange={handleJobChange}
        handleImageChange={handleImageChange}
        handleDownload={handleDownload}
      />
    </div>
  );
}

export default CardEditorWrapper;
