import React from 'react';

function CardPreview(props){
  let currentCard = props.currentCard;

  return (
    <div className="CardViewer">
      <div className="CardViewerBleed"></div>
      <div className="CardViewerText">
        <div className="CardViewerMainText">
          <div>{currentCard.name}</div>
          (<div>{currentCard.age}</div>)
        </div>
        <div className="CardViewerSubText">
          <div>{currentCard.job}</div>
          &nbsp;
          <div className="japaneseName">{currentCard.japaneseName}</div>
        </div>
      </div>
      <div className="CardViewerShadow"></div>
      <img
        draggable={false}
        className="CardViewerImage"
        alt="Background for PDF"
        src={currentCard.image.url}
      />
    </div>
  )
}

export default CardPreview;
