import React, { useState } from 'react';
import { PDFViewer } from '@react-pdf/renderer';

//custom files
import PDFDocument from './PDFDocument';
import InputWrapper from './InputWrapper';
import downloadFile from './downloadFile.js';
import * as CardEditorConstants from './CardEditorConstants.js';

//wrapper for card editor section (including input + PDF)
function CardEditorWrapper(){
  const [name, setName] = useState(CardEditorConstants.defaultName);
  const [japaneseName, setJapaneseName] = useState(CardEditorConstants.defaultJapaneseName);
  const [age, setAge] = useState(CardEditorConstants.defaultAge);
  const [job, setJob] = useState(CardEditorConstants.defaultJob);
  const [image, setImage] = useState(CardEditorConstants.defaultImagePath);

  //PDF document
  const cardDocument = (
    <PDFDocument
      name={name}
      job={job}
      age={age}
      image={image}
      japaneseName={japaneseName}
    />
  )

  //event handlers for inputs
  const handleNameChange = (e) => {
    setName(e.target.value);
  }
  const handleJapaneseNameChange = (e) => {
    setJapaneseName(e.target.value);
  }
  const handleAgeChange = (e) => {
    setAge(e.target.value);
  }
  const handleJobChange = (e) => {
    setJob(e.target.value);
  }
  const handleImageChange = (e) => {
    const tempImage = (e.target.files[0]) ? URL.createObjectURL(e.target.files[0]) : CardEditorConstants.defaultImagePath;
    setImage(tempImage);
  }
  const handleDownload = (e) => {
    downloadFile(cardDocument, name + ".pdf");
  }

  return (
    <div>
      <div className="PDFWrapper" style={{
        height: CardEditorConstants.PDFDimensions.height * 2,
        width: CardEditorConstants.PDFDimensions.width * 2
      }}>
        <PDFViewer scrolling="no" className="PDFViewer">
          {cardDocument}
        </PDFViewer>
      </div>
      <InputWrapper
        handleNameChange={handleNameChange}
        handleJapaneseNameChange={handleJapaneseNameChange}
        handleAgeChange={handleAgeChange}
        handleJobChange={handleJobChange}
        handleImageChange={handleImageChange}
        handleDownload={handleDownload}
      />
    </div>
  );
}

export default CardEditorWrapper;
