import React from 'react';
import { NavLink } from 'react-router-dom';
import { ArrowLeft } from 'react-feather';
import '../../../css/about.css';

const Charades = (props) => {

  return (
    <div>
      <div className="subcontentWrapper">
        <h2 className="subtitle">
          <NavLink className="backToAbout" to="/about"><ArrowLeft />Back</NavLink>
          Charades
        </h2>
        <p>
          Assign the designated players a random Member Card and act out your favorite scenes, the others try to guess who they are.
        </p>
      </div>
      <div className="howToPlayWrapper">
        <p></p>
      </div>
    </div>
  );
}

export default Charades;
