//variables for printing
const DPI = 72;   //(dots per inch)
const bleed = 0.125;
let width = 3.5;
let height = 2.5;

//turn on bleed;
width += bleed;
height += bleed;

//dimensions of the PDF (used to style both PDF and the PDF Wrapper DIV)
//points = inches x dpi (dots per inch)
export const PDFDimensions = {
  width: width * DPI,
  height: height * DPI,
  padding: bleed * DPI,
}

//default card
export const defaultName = "Candace Green";
export const defaultAge = "21";
export const defaultJob = "Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.";
export const defaultImagePath = "/images/test.jpg";
