import React from 'react';

export const EventGoalCard = (props) => {
  let randomDegree = Math.floor((Math.random() * 90) - 45);
  let randomLeft = Math.floor((Math.random() * 80));

  let mainText = "Goal Card";
  let subText = "ゴールカード";
  let color = "red";

  if (props.type === "event"){
    mainText = "Event Card";
    subText = "イベントカード";
    color = "blue";
  }

  return (
    <div className="eventGoalCard footerCard" style={{
      transform:"rotate(" + randomDegree + "deg)",
      left:randomLeft + "vw",
    }}>
      <div className="eventGoalCardText">
        <div className="eventGoalCardMainText">
          {mainText}
        </div>
        <div className="eventGoalCardSubText" style={{background: color}}>
          {subText}
        </div>
      </div>
    </div>
  )
}
