import React from 'react';

//returns the main input field wrapped in a div
function InputWrapper(props) {
  const currentCard = props.currentCard;

  return (
    <div className="InputWrapper">
      <div>
        Katakana Name:
        <input
          type="text"
          onChange={props.handleInputChange}
          name="japaneseName"
          value={currentCard.japaneseName}
        />
      </div>
      <div>
        Job:
        <input
          type="text"
          onChange={props.handleInputChange}
          name="job"
          value={currentCard.job}
        />
      </div>
      <div>
        Image:
        <input
          id="imageFileInput"
          type="file"
          onChange={props.handleInputChange}
          name="image"
          accept="image/*"
        />
        <label htmlFor="imageFileInput">{currentCard.image.name}</label>
      </div>
      <div>
        <button name="download" onClick={props.handleInputChange}>Download All Cards</button>
      </div>
    </div>
  );
}

export default InputWrapper;
