import React from 'react';

//returns the main input field wrapped in a div
function InputWrapper(props) {
  return (
    <div className="InputWrapper">
      <div>
        Name:
        <input
          type="text"
          onChange={props.handleInputChange}
          name="name"
          value={props.currentCard.name}
        />
      </div>
      <div>
        Katakana Name:
        <input
          type="text"
          onChange={props.handleInputChange}
          name="japaneseName"
          value={props.currentCard.japaneseName}
        />
      </div>
      <div>
        Age:
        <input
          type="number"
          onChange={props.handleInputChange}
          name="age"
          value={props.currentCard.age}
        />
      </div>
      <div>
        Job:
        <input
          type="text"
          onChange={props.handleInputChange}
          name="job"
          value={props.currentCard.job}
        />
      </div>
      <div>
        Image:
        <input
          type="file"
          onChange={props.handleInputChange}
          name="image"
          accept="image/*"
          value={props.currentCard.image}
        />
      </div>
      <div>
        <button name="download" onClick={props.handleInputChange}>Download</button>
      </div>
    </div>
  );
}

export default InputWrapper;
