import React, { useState } from 'react';

//custom files
import PDFDocument from './PDFDocument.js';
import downloadPDFFile from '../utils/downloadPDFFile.js';
import { getImageObject } from './CardConstants.js';
import CardViewer from './CardViewer.js';
import InputWrapper from './InputWrapper.js';

//wrapper for card editor section (including input + PDF)
//needs to get via props, the currentCard and setter for changing currentCard properties
function CardEditor(props){
  const currentCard = props.currentCard;
  const setCurrentCard = props.setCurrentCard;

  //returns a new card details object with newValue for newProp
  const updateAndReturnCurrentCard = (newProp, newValue) => {
    return {
      ...currentCard,
      [newProp]: newValue
    }
  }

  //event handlers for inputs
  const handleInputChange = (e) => {
    switch (e.target.name) {
      case 'image':
        if (e.target.files[0]){
          const tempImageURL = URL.createObjectURL(e.target.files[0]);
          const tempImageName = e.target.files[0].name;

          //get new details (width / height) of uploaded image and generate a new image object
          let img = new Image();
          img.onload = function () {
            let tempCard = updateAndReturnCurrentCard("image", getImageObject(
              tempImageURL,
              tempImageName,
              img.width,
              img.height,
            ));
            setCurrentCard(tempCard);
            URL.revokeObjectURL(tempImageURL);
          };
          img.src = tempImageURL;
        }
        break;
      case 'download':
        downloadPDFFile(<PDFDocument
          cards={props.cards}
        />, currentCard.name + ".pdf");
        break;
      default:
        setCurrentCard(updateAndReturnCurrentCard(e.target.name, e.target.value));
        break;
    }
  }

  //handling image movement
  const [startingCoords, setStartingCoords] = useState(false);

  //set starting coordinates on mouse down
  const handleMouseDown = (e) => {
    setStartingCoords({
      x: e.clientX,
      y: e.clientY
    });
  }
  const handleMouseUp = (e) => {
    setStartingCoords(false);
  }
  const handleMouseMove = (e) => {
    let rect = e.target.getBoundingClientRect();
    let x = e.clientX - rect.left; //x position within the element.
    let y = e.clientY - rect.top;  //y position within the element.
    console.log(x, y, e.target.className);
    if (startingCoords){
      // let newX = currentCard.image.viewerX - startingCoords.x - e.clientX;
      // let newY = currentCard.image.viewerY - startingCoords.y - e.clientY;
      // setStartingCoords({
      //   x: e.clientX,
      //   y: e.clientY,
      // });

      //e.clientY where mouse is
      //e.target.clientHeight total height of container
      //currentCard.image.height height of image
      // let tempCard = updateAndReturnCurrentCard("image", {
      //   ...currentCard.image,
      //   viewerX: newX,
      //   viewerY: newY,
      // });
      // setCurrentCard(tempCard);
    }
  }

  return (
    <div>
      <CardViewer
        currentCard={currentCard}
        handleMouseDown={handleMouseDown}
        handleMouseUp={handleMouseUp}
        handleMouseMove={handleMouseMove}
      />
      <InputWrapper
        handleInputChange={handleInputChange}
        currentCard={currentCard}
      />
    </div>
  );
}

export default CardEditor;
