import React, { useEffect } from 'react';
import '../../css/about.css';

const About = () => {

  useEffect(() => {
    document.title = "SHAREHOME - How to play";
  });

  return (
    <div className="content">
      <h1 className="title">SHAREHOME</h1>
      <div className="howToPlayWrapper">
        <h1 className="subtitle">How to play?</h1>
        <p>SHAREHOME is a very flexible party game with multiple ways of play</p>
      </div>
      <div className="howToPlayWrapper">
        <h1 className="subtitle">Roleplaying</h1>
        <p>
          SHAREHOME can be played as a roleplaying game where 6 strangers live together in the same house.
          Their daily interactions and the drama that comes with it are commentated on by the other players.
        </p>
      </div>
    </div>
  );
}

export default About;
