import React from 'react';
import { Document, Page, View, Text, Canvas, Image, Font } from '@react-pdf/renderer';

//import fonts as variables because strings don't seem to work
import InterstateExtraLightCondensed from "../../css/Interstate-ExtraLight-Cond.ttf";
import GenShinGothic from "../../css/GenShinGothic-Monospace-ExtraLight.ttf";

//styles for the PDF document/file
import * as CardConstants from './CardConstants.js';
import styles from '../../css/PDFDocument.js';

//function to paint the shadow background canvas gradient
function paintShadowBackground(painter, availableWidth, availableHeight){
  let grad = painter.linearGradient(0, 0, 0, availableHeight);
  grad.stop(0, "white", 0);
  grad.stop(1, "black", .75);

  painter.rect(0, 0, availableWidth, availableHeight);
  painter.fill(grad);
}

//create a PDF document and use currentCard to change the inside text
function PDFDocument(props){
  const currentCard = props.currentCard;

  //register fonts here so each time document is made, fonts will be good
  Font.register({
    family: 'Interstate Extra Light Condensed',
    fontStyle: 'normal',
    fontWeight: 'normal',
    src: InterstateExtraLightCondensed
  });
  Font.register({
    family: 'Genshin Gothic Monospace ExtraLight',
    fontStyle: 'normal',
    fontWeight: 'normal',
    src: GenShinGothic
  });

  return (
    <Document class="PDFDocument" style={styles.document}>
      <Page
        style={styles.page}
        size={CardConstants.PDFDimensions}
      >
        <View style={styles.view}>

          <Image src={currentCard.image} allowDangerousPaths={true} style={styles.image}/>

          <Canvas style={styles.canvas} paint={paintShadowBackground}></Canvas>

          <Text style={styles.textMain}>
            {currentCard.name} ({currentCard.age})
          </Text>

          <Text style={styles.textSub}>
            {currentCard.job}
            &nbsp;&nbsp;
            <Text style={styles.japaneseName}>{currentCard.japaneseName}</Text>
          </Text>
        </View>
      </Page>
    </Document>
  );
}

export default PDFDocument;
