import React, { useState, useEffect, useRef } from 'react';
import { MemberCard } from './MemberCard.js';
import { EventGoalCard } from './EventGoalCard.js';
import '../../css/footer.css';

//create an array of random cards given type and total amount to make
function createRandomCards(type, refs, totalAll){
  console.log(refs);
  let cards = [...Array(refs.current.length)].map((curr, index)=>{
    let randomLeft = Math.round((Math.random() * 10 - 5) + (index)*(100/refs.current.length));
    let randomBottom = Math.round(Math.random() * -100);
    let randomZ = Math.round(Math.random() * totalAll);
    let randomDegree = Math.floor((Math.random() * 135) - 90);

    let style = {
      left: randomLeft + "vw",
      zIndex: randomZ,
      // bottom: randomBottom,
      // transform: "rotate(" + randomDegree + "deg)",
    }

    switch (type) {
      case "member":
        return (
          <MemberCard
            key={"memberCard" + index}
            style={style}
            ref={refs.current[index]}
          />
        )
      default:
        let randomType = (Math.random() > 0.5) ? "goal" : "event";
        return (
          <EventGoalCard
            type={randomType}
            key={randomType + "Card" + index}
            style={style}
            ref={refs.current[index]}
          />
        )
    }
  });
  return cards;
}

const Footer = (props) => {
  let totalMemberCards = 6;
  let totalEventGoalCards = 3;
  let totalCards = totalMemberCards + totalEventGoalCards;

  const memberRefs = useRef([totalMemberCards]);
  let memberCards = createRandomCards("member", memberRefs, totalCards);

  const eventGoalRefs = useRef([totalEventGoalCards]);
  let eventGoalCard = createRandomCards("eventgoal", eventGoalRefs, totalCards);

  useEffect(()=>{

  });

  return (
    <div className="footer">
      {eventGoalCard}
      {memberCards}
    </div>
  )
}

export default Footer;
