import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';

import Navbar from './components/Navbar/Navbar.js';
import Footer from './components/Footer/Footer.js';
import Home from './components/Home/Home.js';
import About from './components/About/About.js';
import Contact from './components/Contact/Contact.js';
import Designer from './components/Designer/Designer.js';
import Error from './components/Error/Error.js';

const Router = (props) => {

  //state to show the footer or not
  const [showFooter, setShowFooter] = useState(true);

  //set state to show footer with this component
  const renderComponentWithFooter = (Component) => {
    setShowFooter(true);
    return <Component />
  }

  return (
    <BrowserRouter>
      <div>
        <Navbar />
        <Route render={() => {
          if (showFooter){
            //date.now to force re-render each new page click
            return <Footer key={Date.now()}/>
          }
        }} />

        <Switch>
          <Route path="/" render={()=>{
            return renderComponentWithFooter(Home);
          }} exact/>
          <Route path="/about" render={()=>{
            return renderComponentWithFooter(About);
          }} exact/>
          <Route path="/contact" render={()=>{
            return renderComponentWithFooter(Contact);
          }} exact/>
          <Route path="/designer" render={() => {
            //pass the state setter so we can hide the footer when needed
            return <Designer setShowFooter={setShowFooter}/>
          }} exact/>
          <Route render={()=>{
            return renderComponentWithFooter(Error);
          }} />
          <Route render={() => <Redirect to="/" />} />
        </Switch>

      </div>
    </BrowserRouter>
 )
}

export default Router;
