//variables for printing
const DPI = 72;   //(dots per inch)
const bleed = 0.125;
let width = 3.5;
let height = 2.5;
const mainFont = 25;
const subFont = 15;

//turn on bleed;
width += bleed;
height += bleed;

//dimensions of the PDF (used to style both PDF and the PDF Wrapper DIV)
//points = inches x dpi (dots per inch)
//double padding for bleed + real padding
export const PDFDimensions = {
  width: width * DPI,
  height: height * DPI,
  padding: 2 * (bleed * DPI),
  mainFont: mainFont,
  subFont: subFont
}

//default card variables
const defaultName = "Candace Green";
const defaultJapaneseName = "キャンディス•グリーン";
const defaultAge = "21";
const defaultJob = "Influencer";

//default image variables
const defaultImage = {
  url: "/images/test.jpg",
  name: "test.jpg",
  width: 1000,
  height: 1400,
  objectPosition: "50% 50%",
};

export function getDefaultCardObject(){
  return {
    name : defaultName,
    japaneseName : defaultJapaneseName,
    age : defaultAge,
    job : defaultJob,
    image : defaultImage,
  }
}

export function getWidthAndHeight(objectWidth, objectHeight){
  let containerWidth = width * DPI;
  let containerHeight = height * DPI;

  let containerRatio = containerWidth / containerHeight;
  let objectRatio = objectWidth / objectHeight;

  let renderedObject = {};

  if (objectRatio < containerRatio) {
    renderedObject.width = containerWidth;
    renderedObject.height = containerWidth / objectRatio;
  } else {
    renderedObject.width = containerHeight * objectRatio;
    renderedObject.height = containerHeight;
  }

  return renderedObject;
}
