import React from 'react';

function CardPreview(props){
  let currentCard = props.currentCard;

  return (
    <div className="PDFViewer">
      <div className="PDFViewerBleed nopointerevent"></div>
      <div className="PDFViewerText">
        <div className="PDFViewerMainText">
          <div className="PDFViewerInput mainText">{currentCard.name}</div>
          (<div className="PDFViewerInput mainText number">{currentCard.age}</div>)
        </div>
        <div className="PDFViewerSubText">
          <div className="PDFViewerInput">{currentCard.job}</div>
          &nbsp;
          <div className="PDFViewerInput japanese">{currentCard.japaneseName}</div>
        </div>
      </div>
      <div className="PDFViewerShadow nopointerevent"></div>
      <img
        draggable={false}
        className="PDFViewerImage"
        alt="Background for PDF"
        src={currentCard.image.url}
      />
    </div>
  )
}

export default CardPreview;
