import React from 'react';
import '../../css/CardPreview.css';

function CardPreview(props){
  let currentCard = props.currentCard;

  return (
    <div onClick={()=>{
      props.goToCard(props.currentIndex);
    }} className="CardPreview">
      <div className="CardPreviewText">
        <div className="CardPreviewMainText">
          {currentCard.name}({currentCard.age})
        </div>
        <div className="CardPreviewSubText">
          {currentCard.job}&nbsp;<span className="japaneseName">{currentCard.japaneseName}</span>
        </div>
      </div>
      <div className="CardPreviewShadow"></div>
      <img
        draggable={false}
        className="CardPreviewImage"
        alt="Background for Card"
        src={currentCard.image.url}
      />
    </div>
  )
}

export default CardPreview;
