import React from 'react';
import * as CardConstants from './CardConstants.js';

function PDFViewer(props){
  let currentCard = props.currentCard;

  //how much to magnify the HTML PDF viewer
  let magnifyValue = 3;

  let padding = CardConstants.PDFDimensions.padding * magnifyValue;
  let mainFont = CardConstants.PDFDimensions.mainFont * magnifyValue;
  let subFont = CardConstants.PDFDimensions.subFont * magnifyValue;
  let height = CardConstants.PDFDimensions.height * magnifyValue;
  let width = CardConstants.PDFDimensions.width * magnifyValue;

  return (
    <div className="PDFViewer" style={{
      height: height,
      width: width
    }}>
      <div className="PDFViewerText" style={{padding: padding}}>
        <div className="PDFViewerMainText" style={{fontSize: mainFont}}>
          {currentCard.name} (<span>{currentCard.age}</span>)
        </div>
        <div className="PDFViewerSubText" style={{fontSize: subFont}}>
          {currentCard.job} <span>{currentCard.japaneseName}</span>
        </div>
      </div>
      <div className="PDFViewerShadow"></div>
      <img className="PDFViewerImage" alt="Background for PDF" src={currentCard.image} />
    </div>
  )
}

export default PDFViewer;
