import React from 'react';
import { getRandomMember } from './FooterCardConstants.js';

export const MemberCard = () => {
  let currentMember = getRandomMember();
  let randomDegree = Math.floor((Math.random() * 90) - 45);
  let randomLeft = Math.floor((Math.random() * 80));

  return (
    <div className="memberCard" style={{
      transform:"rotate(" + randomDegree + "deg)",
      left:randomLeft + "vw",
    }}>
      <div className="memberCardText">
        <div className="memberCardMainText">
          {currentMember.name} ({currentMember.age})
        </div>
        <div className="memberCardSubText">
          {currentMember.job}&nbsp;<span className="japaneseName">{currentMember.japaneseName}</span>
        </div>
      </div>
      <div className="memberCardShadow"></div>
      <img
        draggable={false}
        className="memberCardImage nopointerevent"
        alt="Background for Card"
        src={currentMember.image.url}
      />
    </div>
  )
}

export const EventCard = () => {
  return (
    <div className="eventCard" style={{
      transform:"rotate(" + randomDegree + "deg)",
      left:randomLeft + "vw",
    }}>
      <div className="eventCardText">
        <div className="eventCardMainText">
          EVENT CARD
        </div>
        <div className="eventCardSubText">
          イベントカード
        </div>
      </div>
    </div>
  )
}

export const GoalCard = () => {
  return (
    <div className="goalCard" style={{
      transform:"rotate(" + randomDegree + "deg)",
      left:randomLeft + "vw",
    }}>
      <div className="goalCardText">
        <div className="goalCardMainText">
          GOAL CARD
        </div>
        <div className="goalCardSubText">
          ゴールカード
        </div>
      </div>
    </div>
  )
}
