import React from 'react';

//returns the main input field wrapped in a div
function InputWrapper(props) {
  return (
    <div className="InputWrapper">
      <div>
        Name: <input type="text" onChange={props.handleNameChange} name="name"/>
      </div>
      <div>
        Image: <input type="file" onChange={props.handleImageChange} name="img" accept="image/*" />
      </div>
    </div>
  );
}

export default InputWrapper;
