import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../../css/about.css';

const About = (props) => {

  let location = useLocation();

  useEffect(() => {
    props.setShowFooter(true);
    document.title = "SHAREHOME - How to play";
  });

  return (
    <div className="content max-width">
      <h1 className="title noselect"> </h1>
      <div>
        <h1 className="subtitle">How to play?</h1>
        <p>SHAREHOME is a very flexible party game with multiple ways of playing.</p>
      </div>
      <div className="howToPlayWrapper">
        <h1 className="subtitle"><a href="/about/roleplay">Roleplay</a></h1>
        <p>
          Play as strangers living together in the same house whose daily interactions and drama are commentated on by the other players.
        </p>
      </div>
      <div className="howToPlayWrapper">
        <h1 className="subtitle"><a href="/about/charades">Charades</a></h1>
        <p>
          Assign the designated players a random Member Card and act out your favorite scenes, the others try to guess who they are.
        </p>
      </div>
      <div className="howToPlayWrapper">
        <h1 className="subtitle"><a href="/about/guesswho">Guess who?</a></h1>
        <p>
          Every player is given a random Member Card and takes turns to guess who they are. You are only allowed to ask yes / no questions.
        </p>
      </div>
      <div className="howToPlayWrapper">
        <h1 className="subtitle"><a href="/about/taboo">Taboo</a></h1>
        <p>
          Split into teams and take turns with the deck of Member Cards. Get your teammates to guess each character without using any of the forbidden words.
        </p>
      </div>
      <div className="howToPlayWrapper">
        <h1 className="subtitle">...and many more!</h1>
        <p>
          How will you play SHAREHOME? <a href="/rulebook.pdf">Click to view the full rulebook PDF.</a>
        </p>
      </div>
    </div>
  );
}

export default About;
