import * as CardConstants from '../components/CardEditor/CardConstants.js';

//how much to magnify the HTML PDF viewer
const magnifyValue = 2;

const padding = CardConstants.PDFDimensions.padding * magnifyValue;
const mainFont = CardConstants.PDFDimensions.mainFont * magnifyValue;
const subFont = CardConstants.PDFDimensions.subFont * magnifyValue;
export const viewerHeight = CardConstants.PDFDimensions.height * magnifyValue;
export const viewerWidth = CardConstants.PDFDimensions.width * magnifyValue;

const PDFViewerDynamicStyles = {
  style: {
    height: viewerHeight,
    width: viewerWidth
  },
  //divide by 2 here because we multiplied by 2 in constants for "real padding"
  bleedStyle: {
    borderRadius:padding / 2,
    height:"calc(100% - " + padding + "px)",
    width:"calc(100% - " + padding + "px)",
  },
  textStyle: {
    padding: padding,
    width:"calc(100% - " + padding * 2 + "px)",
  },
  mainFont: {
    fontSize: mainFont
  },
  subFont: {
    fontSize: subFont
  },
}

export default PDFViewerDynamicStyles;

//function to get an image's width + height after object-fit: cover
export function getCoverFitObjectXY(objectWidth, objectHeight){
  let viewerRatio = viewerWidth / viewerHeight;
  let objectRatio = objectWidth / objectHeight;

  //get width and height of the object after fit
  let tempObjectWidth, tempObjectHeight;
  if (objectRatio < viewerRatio) {
    tempObjectWidth = viewerWidth;
    tempObjectHeight = viewerHeight / objectRatio;
  } else {
    tempObjectWidth = viewerWidth * objectRatio;
    tempObjectHeight = viewerHeight;
  }

  return {
    x: (viewerWidth/2) - (tempObjectWidth/2),
    y: (viewerHeight/2) - (tempObjectHeight/2),
  };
}
