import React, { useLayoutEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import * as serviceWorker from './serviceWorker';
import WebFont from 'webfontloader';

import './css/index.css';
import './css/fonts.css';
import DeckEditor from './components/DeckEditor/DeckEditor.js';

WebFont.load({
  custom: {
    families: ['Interstate Extra Light Condensed', 'GenShinGothic Monospace Extra Light'],
  },
  active: ()=>{
    const targetRef = useRef();
    useLayoutEffect(() => {
      if (targetRef.current) {
        targetRef.current.copyInputStyles();
        targetRef.current.updateInputWidth();
      }
    }, []);
  },
});

ReactDOM.render(
  <React.StrictMode>
    <DeckEditor targetRef={targetRef}/>
  </React.StrictMode>,
  document.getElementById('sharehome')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
