import { PDFViewer } from '@react-pdf/renderer';

export function DebugButtons(){
  return (
    <div className="devtools sidebarContent">
      <button className="sideBarButton" onClick={()=>{console.log(currentCard)}}>Log current card</button>
      <button className="sideBarButton" onClick={()=>{console.log(currentIndex)}}>Log current index</button>
      <button className="sideBarButton" onClick={()=>{console.log(cards)}}>Log cards</button>
    </div>
  )
}

export function DebugPDFViewer(){
  // //debug how PDF looks faster with PDFViewer built into browser, dont have to download file each time
  let embeddedPDF = (
    <div className="EmbeddedPDFViewer" style={{
      width: viewerWidth,
      height: viewerHeight,
      outline: 0,
      border: 0,
      display: "inline-block",
      backgroundColor: "rgb(82, 86, 89)",
    }}></div>
  )
  if (!startingCoords){
    return (
      <div>
        <PDFViewer className="EmbeddedPDFViewer" width={viewerWidth} height={viewerHeight}>
          <PDFDocument cards={props.cards}/>
        </PDFViewer>
      </div>
    )
  }
  else {
    return false;
  }
}
