import React from 'react';
import { Document, Page, View, Text, Canvas, Image, Font, StyleSheet } from '@react-pdf/renderer';

//custom CSS / variables
import '../../css/pdf.css';
import * as CardEditorConstants from './CardEditorConstants.js';

//import and register font
import OpenSansLight from "../../css/OpenSans-CondLight-webfont.ttf";
Font.register({
  family: 'Open Condensed Light',
  fontStyle: 'normal',
  fontWeight: 'normal',
  src: OpenSansLight
});

//styles for actual the PDF document/file
const styles = StyleSheet.create({
  page : {
    width: CardEditorConstants.PDFDimensions.width,
    height: CardEditorConstants.PDFDimensions.height,
    display: "block",
    position: "relative",
  },
  view : {
    display: "inline-block",
    position:"relative",
    height: "100%",
    width: "100%",
  },
  image : {
    objectFit : "cover",
    height : "100%"
  },
  canvas: {
    width: CardEditorConstants.PDFDimensions.width,
    height: CardEditorConstants.PDFDimensions.height/2,
    display: "block",
    position:"absolute",
    bottom:0
  },
  text : {
    fontSize: 25,
    color: "red",
    fontFamily: 'Open Condensed Light',
    position: "absolute",
    zIndex:0,
    left: 0,
    bottom: 0,
    margin: "auto",
    padding: CardEditorConstants.PDFDimensions.padding,
  },
});

//used to paint the shadow background canvas
function paintShadowBackground(painter, availableWidth, availableHeight){
  console.log(painter);
  // Create a linear gradient
  let grad = painter.linearGradient(0, 0, availableWidth, availableHeight);
  console.log(grad);

  painter.rect(0, 0, availableWidth, availableHeight);
  painter.fill(grad);
}

//create a PDF document and use props to change the inside text
function PDFDocument(props){
  return (
    <Document class="PDFDocument" style={styles.document}>
      <Page
        style={styles.page}
        size={CardEditorConstants.PDFDimensions}
      >
        <View style={styles.view}>
          <Image src={props.image} style={styles.image}/>
          <Canvas style={styles.canvas} paint={paintShadowBackground}></Canvas>
          <Text style={styles.text}>
            {props.name}
          </Text>
        </View>
      </Page>
    </Document>
  );
}

export default PDFDocument;
