//variables for printing
const DPI = 72;   //(dots per inch)
const bleed = 0.125;
let width = 3.5;
let height = 2.5;

//turn on bleed;
width += bleed;
height += bleed;

//dimensions of the PDF (used to style both PDF and the PDF Wrapper DIV)
//points = inches x dpi (dots per inch)
export const PDFDimensions = {
  width: width * DPI,
  height: height * DPI,
  padding: bleed * DPI,
}

//default name that appears on load
export const defaultName = "TERRACE HOUSE";

//default image that appears on load
export const defaultImagePath = "../../images/test.jpeg";
