import React, { useState, useReducer } from 'react';

//custom files
import CardEditorWrapper from '../CardEditor/CardEditorWrapper.js';
import * as CardConstants from '../CardEditor/CardConstants.js';

//reducer to change state in react
//to add a new card use dispatch({ type: 'add', item: movie })
//to remove an existing card use dispatch({ type: 'remove', index })
//to update an existing card use dispatch({ type: 'update', index, value, property })
function reducer(state, action) {
  switch (action.type) {
    case 'add':
      return [...state, action.card];
    case 'remove':
      return state.filter((item, index) => {
        //remove item at index
        if(index === action.index) {
          return false;
        }
        // //remove item with property with value
        // else if (item[action.property] === action.value){
        //   return false;
        // }
        //every other item stays
        return true;
      });
    case 'update':
      return state.map((item, index) => {
        //replace the item at index
        if(index === action.index) {
          return action.card;
        }
        //leave every other item unchanged
        return item;
      });
    case 'reset':
      return [action.card];
    default:
      throw new Error();
  }
}

//wrapper for card editor section (including input + PDF)
let currentCard = CardConstants.getDefaultCardObject();
function DeckEditorWrapper(){
  const [cards, dispatch] = useReducer(reducer, [currentCard]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [, updateState] = React.useState();
  const forceUpdate = React.useCallback(() => updateState({}), []);

  //updates the current card and updates it in the cards array
  const updateCurrentCard = (card) => {
    currentCard = card;
    dispatch({
      type: 'update',
      index: currentIndex,
      card: currentCard
    });

    //if i want to implement an undo, i have access to what the card WAS here
    //just need to keep this in an array and access it whenever
    // console.log(cards[currentIndex]);
  }

  //deletes all cards and resets index to 0
  const resetAllCards = () => {
    setCurrentIndex(0);
    currentCard = CardConstants.getDefaultCardObject();
    dispatch({
      type: 'reset',
      card: currentCard
    });
  }

  //adds a new card and sets the index to last
  const addNewCard = () => {
    setCurrentIndex(currentIndex++);
    currentCard = CardConstants.getDefaultCardObject();
    dispatch({
      type: 'add',
      card: currentCard
    });
  }

  //deletes the current card and sets the index to last
  const removeCurrentCard = () => {
    setCurrentIndex(currentIndex--);
    //if there are no more cards left, just reset
    if (currentIndex < 0){
      resetAllCards();
    }
    else {
      currentCard = cards[currentIndex];
      dispatch({
        type: 'remove',
        index: currentIndex
      });
    }
  }

  const goToCard = (cardIndex) => {
    setCurrentIndex(cardIndex);
    currentCard = cards[cardIndex];
  }

  const listOfCards = cards.map((step, move) => {
    const desc = 'Go to card #' + (move + 1);
    return (
      <li key={"card"+move}>
        <button onClick={() => goToCard(move)}>{desc}</button>
      </li>
    );
  });

  return (
    <div>
      <CardEditorWrapper
        currentCard={currentCard}
        setCurrentCard={updateCurrentCard}
      />
      <button onClick={resetAllCards}>Reset All</button>
      <button onClick={addNewCard}>Add New Card</button>
      <button onClick={removeCurrentCard}>Remove Current Card</button>
      <ol>
        {listOfCards}
      </ol>
      <div>
        Dev tools
        <button onClick={()=>{console.log(currentCard)}}>Log current card</button>
        <button onClick={()=>{console.log(currentIndex)}}>Log current index</button>
        <button onClick={()=>{console.log(cards)}}>Log cards</button>
        </div>
    </div>
  );
}

export default DeckEditorWrapper;
