import React from 'react';
import HowToPlaySubtitle from './HowToPlaySubtitle.js';
import { EventGoalCard } from '../../Footer/EventGoalCard.js';
import { MemberCard } from '../../Footer/MemberCard.js';
import { Gift, Repeat } from 'react-feather';
import '../../../css/about.css';

const Charades = (props) => {

  let cardStyle = {
    width:"210px",
    height:"150px",
    fontSize:"6px",
  }

  return (
    <div>
      <HowToPlaySubtitle
        subtitle="Charades"
        description="Players take turns acting out their favorite scenes with random Member Cards, while the others try to guess who they are."
      />

      <div className="stepWrapper">
        <h2 className="subtitle">Step 1</h2>
        <p>Select a random Event Card. Select players at random based on the amount of participants on the Event Card.</p>
        <EventGoalCard
          type="event"
          mainStyle={cardStyle}
        />
      </div>

      <div className="stepWrapper">
        <h2 className="subtitle">Step 2</h2>
        <p>Players are given a random Member Card. They act out the scene as if they are that Member.</p>
        <MemberCard mainStyle={cardStyle} />
      </div>

      <div className="stepWrapper">
        <h2 className="subtitle">Step 3</h2>
        <p>Whoever first guesses all the Members correctly earns a point!</p>
        <Gift />
      </div>

      <div className="stepWrapper">
        <h2 className="subtitle">Step 4</h2>
        <p>Repeat from Step 1 until bored.</p>
        <Repeat />
      </div>

    </div>
  );
}

export default Charades;
