import React from 'react';
import HowToPlaySubtitle from './HowToPlaySubtitle.js';
import { MemberCard } from '../../Footer/MemberCard.js';
import { ChevronDown, Users } from 'react-feather';
import '../../../css/about.css';

const Roleplay = (props) => {

  return (
    <div>
      <HowToPlaySubtitle
        subtitle="Roleplay"
        description="Play as strangers living together in the same house whose daily interactions and drama are commentated on by the other players."
      />

      <div className="stepWrapper">
        <h2 className="subtitle">Step 1</h2>
        <p>Decide the 6 players who will be Members of the House. The rest of the players will be playing as Commentators</p>
        <div className="illustrationWrapperCenter">
          <div>
            <Users stroke="#03C04E" />
            <Users stroke="#03C04E" />
            <Users stroke="#03C04E" />
          </div>
          <div>
            <Users stroke="#F9C22E" className="is-flipped" />
            <Users stroke="#F9C22E" className="is-flipped" />
            <Users stroke="#F9C22E" className="is-flipped" />
          </div>
        </div>
      </div>

      <div className="stepSplitterLine"><ChevronDown /></div>

    </div>
  );
}

export default Roleplay;
