import React, { useEffect } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import Roleplay from './HowToPlayComponents/Roleplay.js';
import Charades from './HowToPlayComponents/Charades.js';
import Guesswho from './HowToPlayComponents/Guesswho.js';
import Taboo from './HowToPlayComponents/Taboo.js';
import '../../css/about.css';

const About = (props) => {

  let location = useLocation();
  let Content = () => {
    switch (location.pathname){
      case "/about/roleplay":
        return <Roleplay />;
      case "/about/charades":
        return <Charades />;
      case "/about/guesswho":
        return <Guesswho />;
      case "/about/taboo":
        return <Taboo />;
      default:
        return (
          <div>
            <div className="subcontentWrapper">
              <h2 className="subtitle">How to play?</h2>
              <p>SHAREHOME is a very flexible party game with multiple ways of playing.</p>
            </div>
            <div className="howToPlayWrapper">
              <h2 className="subtitle"><NavLink to="/about/roleplay">Roleplay</NavLink></h2>
              <p>
                Play as strangers living together in the same house whose daily interactions and drama are commentated on by the other players.
              </p>
            </div>
            <div className="howToPlayWrapper">
              <h2 className="subtitle"><NavLink to="/about/charades">Charades</NavLink></h2>
              <p>
                Assign the designated players a random Member Card and act out your favorite scenes, the others try to guess who they are.
              </p>
            </div>
            <div className="howToPlayWrapper">
              <h2 className="subtitle"><NavLink to="/about/guesswho">Guess who?</NavLink></h2>
              <p>
                Every player is given a random Member Card and takes turns to guess who they are. You are only allowed to ask yes / no questions.
              </p>
            </div>
            <div className="howToPlayWrapper">
              <h2 className="subtitle"><NavLink to="/about/taboo">Taboo</NavLink></h2>
              <p>
                Split into teams and take turns with the deck of Member Cards. Get your teammates to guess each character without using any of the forbidden words.
              </p>
            </div>
            <div className="howToPlayWrapper">
              <h2 className="subtitle">...and many more!</h2>
              <p>
                How will you play SHAREHOME? <a href="/rulebook.pdf">Click to view the full rulebook PDF.</a>
              </p>
            </div>
          </div>
        )
    }
  }

  useEffect(() => {
    props.setShowFooter(true);
    document.title = "SHAREHOME - How to play";
  });

  return (
    <div className="content max-width">
      <NavLink to="/"><div className="title noselect"></div></NavLink>
      <Content />
    </div>
  );
}

export default About;
