import { saveAs } from 'file-saver';
import { pdf } from '@react-pdf/renderer';

const downloadFileButton = async (documentData) => {
  const blob = await pdf(documentData).toBlob();
  saveAs(blob, fileName);
};

export default downloadFileButton;
