import React, { useState, useEffect } from 'react';

//custom files
import DeckEditor from './DeckEditor/DeckEditor.js';

function Designer(){

  const [startedDesigning, setStartedDesigning] = useState(false);

  const startDesigning = (e) =>{
    setStartedDesigning(true);
  }

  useEffect(()=>{
    document.title = "SHAREHOME - Card Designer";
  });

  return (
    <div>
      <div className="content max-width">
        <h1 className="title noselect"> </h1>
        <h1 className="subtitle">Design your own cards</h1>
        <p>
          Design custom cards of you and your friends to play with. Share the cards with the world so that anyone can play with them. The possibilities are endless!
        </p>
        <button className="button" onClick={startDesigning}>Click me to Start</button>

        {startedDesigning &&
          <DeckEditor />
        }
      </div>
    </div>
  );
}

export default Designer;
