import React from 'react';
import PDFViewerDynamicStyles from '../../css/PDFViewerDynamicStyles.js';

function CardViewer(props){
  let currentCard = props.currentCard;

  return (
    <div
      className="PDFViewer noselect" 
      style={PDFViewerDynamicStyles.style}
      onMouseDown={props.handleMouseDownImage}
      onMouseUp={props.handleMouseUpImage}
    >
      <div className="PDFViewerBleed" style={PDFViewerDynamicStyles.bleedStyle}></div>
      <div className="PDFViewerText" style={PDFViewerDynamicStyles.textStyle}>
        <div className="PDFViewerMainText" style={PDFViewerDynamicStyles.mainFont}>
          {currentCard.name} (<span>{currentCard.age}</span>)
        </div>
        <div className="PDFViewerSubText" style={PDFViewerDynamicStyles.subFont}>
          {currentCard.job} <span>{currentCard.japaneseName}</span>
        </div>
      </div>
      <div className="PDFViewerShadow"></div>
      <img className="PDFViewerImage" alt="Background for PDF" src={currentCard.image} />
    </div>
  )
}

export default CardViewer;
