import React from 'react';

export const EventGoalCard = (props) => {
  let mainText = "Goal Card";
  let subText = "ゴールカード";
  let color = "blue";

  if (props.type === "event"){
    mainText = "Event \n Card";
    subText = "イベントカード";
    color = "red";
  }

  return (
    <div id={props.id} className="eventGoalCard footerCard" style={props.mainStyle}>
      <div className="eventGoalCardText">
        <div className="eventGoalCardMainText" style={{
          fontSize: "6.5vh",
          padding: "1vh",
        }}>
          {mainText}
        </div>
        <div className="eventGoalCardSubText" style={{
          background: color,
          fontSize: "2.5vh",
          padding: "1vh",
        }}>
          {subText}
        </div>
      </div>
    </div>
  )
}
