import React, { useState, useReducer } from 'react';

//custom files
import CardEditorWrapper from '../CardEditor/CardEditorWrapper.js';
import * as CardConstants from '../CardEditor/CardConstants.js';

//reducer to change state in react
//to add a new card use dispatch({ type: 'add', item: movie })
//to remove an existing card use dispatch({ type: 'remove', index })
//to update an existing card use dispatch({ type: 'remove', index, value, property })
function reducer(state, action) {
  switch (action.type) {
    case 'add':
      return [...state, CardConstants.getDefaultCardObject()];
    case 'remove':
      return [
        ...state.slice(0, action.index),
        ...state.slice(action.index + 1)
      ];
    case 'update':
      let updatedItem = state[action.index];
      updatedItem[action.property] = action.value;
      return [
        ...state.slice(0, action.index),
        updatedItem,
        ...state.slice(action.index + 1)
      ]
    case 'reset':
      return [CardConstants.getDefaultCardObject()];
    default:
      throw new Error();
  }
}

//wrapper for card editor section (including input + PDF)
function DeckEditorWrapper(){
  const [cards, dispatch] = useReducer(reducer, [CardConstants.getDefaultCardObject()]);
  const [currentCard, setCurrentCard] = useState(cards[0]);

  const resetAllCards = () => {
    dispatch({ type: 'reset'});
    setCurrentCard(cards[0]);
  }

  const addNewCard = () => {
    dispatch({ type: 'add'});
    setCurrentCard(cards[cards.length-1]);
  }

  return (
    <div>
      <CardEditorWrapper
        currentCard={currentCard}
        setCurrentCard={setCurrentCard}
      />
      <button onClick={resetAllCards}>Reset All</button>
      <button onClick={addNewCard}>Add New Card</button>
      <button onClick={()=>{console.log(cards)}}>Log cards</button>
    </div>
  );
}

export default DeckEditorWrapper;
