import React, { useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Smile, Gift, Printer } from 'react-feather';
import '../../css/home.css';
import '../../css/colors.css';

const Home = (props) => {

  useEffect(() => {
    props.setShowFooter(true);
    document.title = "SHAREHOME - A custom party game";
  });

  return (
    <div className="content">
      <NavLink to="/"><div className="title noselect"></div></NavLink>
      <p className="subtitle">
        A custom party game inspired by Terrace House
      </p>
      <div className="CTAWrapper">
        <NavLink className="greenBorder callToAction noselect" to="/play">
          <h1>Play virtually right now</h1>
          <Smile />
          <p>(friends or video conferencing not included)</p>
        </NavLink>
        <NavLink className="redBorder callToAction noselect" to="/purchase">
          <h1>Purchase a physical copy</h1>
          <Gift />
          <p>(for when quarantine is finally over)</p>
        </NavLink>
        <NavLink className="yellowBorder callToAction noselect" to="/print">
          <h1>Print a shitty paper version</h1>
          <Printer />
          <p>(free if you somehow have a printer)</p>
        </NavLink>
      </div>
    </div>
  );
}

export default Home;
