import React from 'react';

//custom files
import * as CardConstants from './CardConstants.js';
import PDFDocument from './PDFDocument.js';
import downloadPDFFile from '../utils/downloadPDFFile.js';
import CardViewer from './CardViewer.js';
import InputWrapper from './InputWrapper.js';

//wrapper for card editor section (including input + PDF)
//needs to get via props, the currentCard and setter for changing currentCard properties
function CardEditor(props){
  const currentCard = props.currentCard;
  const setCurrentCard = props.setCurrentCard;

  //returns a new card details object with newValue for newProp
  const updateCurrentCard = (newProp, newValue, object) => {
    let tempObject = (object) ? object : currentCard;
    return {
      ...tempObject,
      [newProp]: newValue
    }
  }

  //event handlers for inputs
  const handleInputChange = (e) => {
    switch (e.target.name) {
      case 'image':
        const tempImage = (e.target.files[0]) ? URL.createObjectURL(e.target.files[0]) : CardConstants.defaultImage;
        const tempImageName = (e.target.files[0]) ? e.target.files[0].name : CardConstants.defaultImage;

        let tempCard = updateCurrentCard("image", tempImage);
        tempCard = updateCurrentCard("imageName", tempImageName, tempCard);

        setCurrentCard(tempCard);
        break;
      case 'download':
        downloadPDFFile(<PDFDocument
          cards={cards}
        />, currentCard.name + ".pdf");
        break;
      default:
        setCurrentCard(updateCurrentCard(e.target.name, e.target.value));
        break;
    }
  }

  return (
    <div>
      <CardViewer currentCard={currentCard} />
      <InputWrapper
        handleInputChange={handleInputChange}
        currentCard={currentCard}
      />
    </div>
  );
}

export default CardEditor;
