import React from 'react';
import HowToPlaySubtitle from './HowToPlaySubtitle.js';
import { EventGoalCard } from '../../Footer/EventGoalCard.js';
import { MemberCard } from '../../Footer/MemberCard.js';
import { HelpCircle, Gift, Repeat } from 'react-feather';
import '../../../css/about.css';

const Guesswho = (props) => {

  let cardStyle = {
    width:"210px",
    height:"150px",
    fontSize:"6px",
  }

  return (
    <div>
      <HowToPlaySubtitle
        subtitle="Guess Who?"
        description="Every player is given a random Member Card and takes turns to guess who they are. You are only allowed to ask yes / no questions."
      />

      <div className="stepWrapper">
        <h2 className="subtitle">Step 1</h2>
        <p>Every player is given a random Member Card.</p>
        <div className="illustrationWrapperCenter">
          <MemberCard
            mainStyle={cardStyle}
          />
        </div>
      </div>

      <div className="stepSplitterLine"><ChevronDown /></div>

      <div className="stepWrapper">
        <h2 className="subtitle">Step 2</h2>
        <p>Take turns asking a single yes / no question about your assigned Member.</p>
        <div className="illustrationWrapperCenter">
          <HelpCircle />
        </div>
      </div>

      <div className="stepSplitterLine"><ChevronDown /></div>

      <div className="stepWrapper">
        <h2 className="subtitle">Step 3</h2>
        <p>Whoever first guesses their Member correctly earns a point!</p>
        <div className="illustrationWrapperCenter">
          <Gift />
        </div>
      </div>
      <p>Repeat from Step 1 until bored.</p>

    </div>
  );
}

export default Guesswho;
