import React, { useState, useEffect } from 'react';
import { MemberCard } from './MemberCard.js';
import { EventGoalCard } from './EventGoalCard.js';
import '../../css/footer.css';

//create an array of random cards given type and total amount to make
function createRandomCards(type, totalSame, totalAll){
  let cards = [...Array(totalSame)].map((curr, index)=>{
    let randomLeft = Math.round((Math.random() * 10 - 5) + (index)*(100/totalSame));
    let randomBottom = Math.round(Math.random() * -100);
    let randomZ = Math.round(Math.random() * totalAll);
    let randomDegree = Math.floor((Math.random() * 135) - 90);

    let tempType = (type === "member") ? "member" : (Math.random() > 0.5) ? "goal" : "event";
    let props = {
      key: tempType + index,
      id: type + index,
      type: tempType,
      style: {
        left: randomLeft + "vw",
        zIndex: randomZ,
      },
      randomBottom: randomBottom,
      randomDegree: randomDegree,
    }

    return (type === "member") ? (<MemberCard {...props}/>) : (<EventGoalCard {...props}/>);
  });
  return cards;
}

const Footer = (props) => {
  let totalMemberCards = 6;
  let totalEventGoalCards = 3;
  let totalCards = totalMemberCards + totalEventGoalCards;

  let memberCards = createRandomCards("member", totalMemberCards, totalCards);
  let eventGoalCard = createRandomCards("eventgoal", totalEventGoalCards, totalCards);

  useEffect(()=>{
    for (let i = 0; i < memberCards.length; i++){
      document.getElementById("member" + i).style.bottom = memberCards[i].props.randomBottom + "px";
      document.getElementById("member" + i).style.transform = "rotate(" + memberCards[i].props.randomDegree + "deg)";
    }
    for (let i = 0; i < eventGoalCard.length; i++){
      document.getElementById("eventgoal" + i).style.bottom = memberCards[i].props.randomBottom + "px";
      document.getElementById("eventgoal" + i).style.transform = "rotate(" + memberCards[i].props.randomDegree + "deg)";
    }
  });

  return (
    <div className="footer">
      {eventGoalCard}
      {memberCards}
    </div>
  )
}

export default Footer;
