import React from 'react';
import { NavLink } from 'react-router-dom';
import { ArrowLeft, Users, Clock} from 'react-feather';

//the subtitle with the description and the title in how-to-play page
const HowToPlaySubtitle = (props) => {

  return (
    <div className="subcontentWrapper border-bottom">
      <h2 className="subtitle">
        <NavLink className="backToAbout" to="/about"><ArrowLeft />Back</NavLink>
        {props.subtitle}
      </h2>
      <div class="illustrationWrapperCenter">
        <p class="subtext">
          <Users />7+ Players
          <Clock />10 minutes
        </p>
      </div>
      <p>
        {props.description}
      </p>
    </div>
  );
}

export default HowToPlaySubtitle;
