import * as CardConstants from '../components/CardEditor/CardConstants.js';

//how much to magnify the HTML PDF viewer
const magnifyValue = 2;

const padding = CardConstants.PDFDimensions.padding * magnifyValue;
const mainFont = CardConstants.PDFDimensions.mainFont * magnifyValue;
const subFont = CardConstants.PDFDimensions.subFont * magnifyValue;
const height = CardConstants.PDFDimensions.height * magnifyValue;
const width = CardConstants.PDFDimensions.width * magnifyValue;

const PDFViewerDynamicStyles = {
  style: {
    height: height,
    width: width
  },
  //divide by 2 here because we multiplied by 2 in constants for "real padding"
  bleedStyle: {
    borderRadius:padding / 2,
    height:"calc(100% - " + padding + "px)",
    width:"calc(100% - " + padding + "px)",
  },
  textStyle: {
    padding: padding,
    width:"calc(100% - " + padding * 2 + "px)",
  },
  mainFont: {
    fontSize: mainFont
  },
  subFont: {
    fontSize: subFont
  },
}

export default PDFViewerDynamicStyles;

//function to get an image's width + height after object-fit
export function getWidthAndHeight(objectWidth, objectHeight){
  let containerRatio = width / height;
  let objectRatio = objectWidth / objectHeight;

  let renderedObject = {};

  if (objectRatio < containerRatio) {
    renderedObject.width = width;
    renderedObject.height = width / objectRatio;
  } else {
    renderedObject.width = height * objectRatio;
    renderedObject.height = objectHeight / containerRatio;
  }

  return renderedObject;
}
