import React from 'react';
import PDFViewerDynamicStyles from '../../css/PDFViewerDynamicStyles.js';
import AutosizeInput from 'react-input-autosize';

function CardViewer(props){
  let currentCard = props.currentCard;

  return (
    <div>
      <div>
        <span id="target" className="PDFHiddenName" style={PDFViewerDynamicStyles.mainFont}>{currentCard.name}</span>
      </div>
      <div
        className="PDFViewer noselect"
        style={PDFViewerDynamicStyles.style}
        onMouseOut={props.handleMouseUp}
      >
        <div className="PDFViewerBleed nopointerevent" style={PDFViewerDynamicStyles.bleedStyle}></div>
        <div className={"PDFViewerText " + props.textNoPointerEvent} style={PDFViewerDynamicStyles.textStyle}>
          <div className="PDFViewerMainText" style={PDFViewerDynamicStyles.mainFont}>
            <AutosizeInput
              name="name"
              ref={props.targetRef}
              className="PDFViewerInput"
              inputStyle={PDFViewerDynamicStyles.mainFont}
              type="text"
              onChange={props.handleInputChange}
              value={currentCard.name}
              placeholder={currentCard.name}
            />
            (<AutosizeInput
                name="age"
                className="PDFViewerInput number"
                inputStyle={PDFViewerDynamicStyles.mainFont}
                type="number"
                extraWidth={0}
                onChange={props.handleInputChange}
                value={currentCard.age}
                placeholder={currentCard.age}
            />)
          </div>
          <div className="PDFViewerSubText" style={PDFViewerDynamicStyles.subFont}>
            {currentCard.job} <span>{currentCard.japaneseName}</span>
          </div>
        </div>
        <div className="PDFViewerShadow nopointerevent"></div>
        <img
          draggable={false}
          className="PDFViewerImage"
          alt="Background for PDF"
          src={currentCard.image.url}
          style={{
            objectPosition: currentCard.image.viewerX + "px " + currentCard.image.viewerY + "px"
          }}
          onMouseDown={props.handleMouseDown}
          onMouseUp={props.handleMouseUp}
          onMouseMove={props.handleMouseMove}
        />
      </div>
    </div>
  )
}

export default CardViewer;
