import React from 'react';
import { Document, Page, View, Text, Canvas, Image, Font, StyleSheet } from '@react-pdf/renderer';

//custom CSS / variables
import '../../css/pdf.css';
import * as CardEditorConstants from './CardEditorConstants.js';

import InterstateExtraLightCondensed from "../../css/Interstate-ExtraLight-Cond.ttf";
import Genshin from "../../css/GenShinGothic-Monospace-Light.ttf";
Font.register({
  family: 'Interstate Extra Light Condensed',
  fontStyle: 'normal',
  fontWeight: 'normal',
  src: InterstateExtraLightCondensed
});
Font.register({
  family: 'Nasu Regular',
  fontStyle: 'normal',
  fontWeight: 'normal',
  src: Genshin
});

//styles for actual the PDF document/file
const styles = StyleSheet.create({
  page : {
    width: CardEditorConstants.PDFDimensions.width,
    height: CardEditorConstants.PDFDimensions.height,
    display: "block",
    position: "relative",
    color: "white",
    fontFamily: 'Interstate Extra Light Condensed',
  },
  view : {
    display: "inline-block",
    position:"relative",
    height: "100%",
    width: "100%",
    whiteSpace: "nowrap",
  },
  image : {
    objectFit : "cover",
    height : "100%"
  },
  canvas: {
    width: CardEditorConstants.PDFDimensions.width,
    height: CardEditorConstants.PDFDimensions.height/2,
    display: "block",
    position:"absolute",
    bottom:0
  },
  textMain : {
    fontSize: 25,
    textTransform: "uppercase",
    position: "absolute",
    left: 0,
    bottom: 25,
    padding: CardEditorConstants.PDFDimensions.padding,
    paddingBottom: CardEditorConstants.PDFDimensions.padding/2,
    maxLines: 1,
  },
  textSub : {
    fontSize: 15,
    position: "absolute",
    left: 0,
    bottom: 0,
    padding: CardEditorConstants.PDFDimensions.padding,
    paddingTop: 0,
    maxLines: 1,
  },
  japaneseName : {
    fontFamily: "Nasu Regular",
    color: "white",
    fontSize: 15
  },
});

//used to paint the shadow background canvas gradient
function paintShadowBackground(painter, availableWidth, availableHeight){
  let grad = painter.linearGradient(0, 0, 0, availableHeight);
  grad.stop(0, "white", 0);
  grad.stop(1, "black", .75);

  painter.rect(0, 0, availableWidth, availableHeight);
  painter.fill(grad);
}

//create a PDF document and use props to change the inside text
function PDFDocument(props){
  return (
    <Document class="PDFDocument" style={styles.document}>
      <Page
        style={styles.page}
        size={CardEditorConstants.PDFDimensions}
      >
        <View style={styles.view}>

          <Image src={props.image} allowDangerousPaths={true} style={styles.image}/>

          <Canvas style={styles.canvas} paint={paintShadowBackground}></Canvas>

          <Text style={styles.textMain}>
            {props.name} ({props.age})
          </Text>

          <Text style={styles.textSub}>
            {props.job}&nbsp;<Text stlye={styles.japaneseName}>{props.japaneseName}</Text>
          </Text>
        </View>
      </Page>
    </Document>
  );
}

export default PDFDocument;
