import React, { useState, useReducer } from 'react';

//custom files
import * as CardConstants from '../CardEditor/CardConstants.js';
import '../../css/DeckEditor.css';
import { DebugButtons } from '../utils/DebugTools.js';
import downloadPDFFile from '../utils/downloadPDFFile.js';
import PDFDocument from './PDFDocument.js';
import CardPreview from './CardPreview.js';
import CardEditor from '../CardEditor/CardEditor.js';

//reducer to change state in react
//to add a new card use dispatch({ type: 'add', item: movie })
//to remove an existing card use dispatch({ type: 'remove', index })
//to update an existing card use dispatch({ type: 'update', index, value, property })
function reducer(state, action) {
  switch (action.type) {
    case 'add':
      return [...state, action.card];
    case 'remove':
      return state.filter((item, index) => {
        //remove item at index
        if(index === action.index) {
          return false;
        }
        // //remove item with property with value
        // else if (item[action.property] === action.value){
        //   return false;
        // }
        //every other item stays
        return true;
      });
    case 'update':
      return state.map((item, index) => {
        //replace the item at index
        if(index === action.index) {
          return action.card;
        }
        //leave every other item unchanged
        return item;
      });
    case 'reset':
      return [action.card];
    default:
      throw new Error();
  }
}

//wrapper for card editor section (including input + PDF)
let currentCard = CardConstants.getDefaultCardObject();
function DeckEditor(){
  const [cards, dispatch] = useReducer(reducer, [currentCard]);
  const [currentIndex, setCurrentIndex] = useState(0);

  //updates the current card and updates it in the cards array
  const updateCurrentCard = (card) => {
    currentCard = card;
    dispatch({
      type: 'update',
      index: currentIndex,
      card: currentCard
    });

    //if i want to implement an undo, i have access to what the card WAS here
    //just need to keep this in an array and access it whenever
    // console.log(cards[currentIndex]);
  }

  //download the PDF file
  const downloadAllCards = () => {
    downloadPDFFile(<PDFDocument
      cards={cards}
    />, "SHAREHOME_Deck.pdf");
  }

  //deletes all cards and resets index to 0
  const resetAllCards = () => {
    currentCard = CardConstants.getDefaultCardObject();
    dispatch({
      type: 'reset',
      card: currentCard
    });
    setCurrentIndex(0);
  }

  //adds a new card and sets the index to last
  const addNewCard = () => {
    currentCard = CardConstants.getDefaultCardObject();
    dispatch({
      type: 'add',
      card: currentCard
    });
    setCurrentIndex(cards.length);
  }

  //deletes the current card and sets the index to last
  const removeCurrentCard = () => {
    const newLength = cards.length - 1;
    //if there are no more cards left, just reset
    if (newLength <= 0){
      resetAllCards();
    }
    else {
      currentCard = cards[currentIndex - 1];
      setCurrentIndex(currentIndex - 1);
      dispatch({
        type: 'remove',
        index: currentIndex
      });
    }
  }

  //renders a specific card
  const goToCard = (cardIndex) => {
    setCurrentIndex(cardIndex);
    currentCard = cards[cardIndex];
  }

  //renders a list of buttons to keep track of all cards in the deck (click to go to it)
  const listOfCards = cards.map((step, move) => {
    let className = "cardPreview";

    //current card is this button!
    if (move === currentIndex){
      className = className + " currentCard";
    }

    return (
      <CardPreview
        className={className}
        currentCard={step}
        currentIndex={move}
        handleClick={goToCard}
      />
    );
  });

  return (
    <div>
      <div className="sidebar">
        <div className="sidebarButtonWrapper sidebarContent">
          <button className="sideBarButton" onClick={resetAllCards}>Reset All Cards</button>
          <button className="sideBarButton" onClick={addNewCard}>Add New Card</button>
          <button className="sideBarButton" onClick={removeCurrentCard}>Remove Current Card</button>
          <button className="sideBarButton" onClick={downloadAllCards}>Download All Cards</button>
        </div>
        <DebugButtons
          currentCard={currentCard}
          currentIndex={currentIndex}
          cards={cards}
        />
        <div className="cardPreviewWrapper sidebarContent">
        {listOfCards}
        </div>
      </div>
      <div className="content">
        <CardEditor
          cards={cards}
          currentCard={currentCard}
          setCurrentCard={updateCurrentCard}
        />
      </div>
    </div>
  );
}

export default DeckEditor;
