import React, { useState } from 'react';
import PDFDocument from '../DeckEditor/PDFDocument.js';
import { PDFViewer } from '@react-pdf/renderer';

//custom files
import { getImageObject, magnifyValue } from './CardConstants.js';
import CardViewer from './CardViewer.js';
import { viewerHeight, viewerWidth } from '../../css/CardViewerDynamicStyles.js';
import InputWrapper from './InputWrapper.js';

//get the X and Y coordinates of mouse inside the bounding rect
function getMouseXY(e){
  let rect = e.currentTarget.getBoundingClientRect();
  return {
    x: e.clientX - rect.left, //x position within the element.
    y: e.clientY - rect.top,  //y position within the element.
  }
}

//wrapper for card editor section (including input + PDF)
//needs to get via props, the currentCard and setter for changing currentCard properties
function CardEditor(props){
  const currentCard = props.currentCard;
  const setCurrentCard = props.setCurrentCard;

  //returns a new card details object with newValue for newProp
  const updateAndReturnCurrentCard = (newProp, newValue) => {
    return {
      ...currentCard,
      [newProp]: newValue
    }
  }

  //event handlers for inputs
  const handleInputChange = (e) => {
    switch (e.target.name) {
      case 'image':
        if (e.target.files[0]){
          const tempImageURL = URL.createObjectURL(e.target.files[0]);
          const tempImageName = e.target.files[0].name;

          //get new details (width / height) of uploaded image and generate a new image object
          let img = new Image();
          img.onload = function () {
            let tempCard = updateAndReturnCurrentCard("image", getImageObject(
              tempImageURL,
              tempImageName,
              img.width,
              img.height,
            ));
            setCurrentCard(tempCard);
          };
          img.src = tempImageURL;
        }
        break;
      default:
        setCurrentCard(updateAndReturnCurrentCard(e.target.name, e.target.value));
        break;
    }
  }

  //handling image movement
  const [startingCoords, setStartingCoords] = useState(false);

  //set starting coordinates on mouse down
  const handleMouseDown = (e) => {
    let mouseXY = getMouseXY(e);
    setStartingCoords({
      x: mouseXY.x,
      y: mouseXY.y
    });
  }
  const handleMouseUp = (e) => {
    setStartingCoords(false);
  }
  const handleMouseMove = (e) => {
    //if mouse down
    if (startingCoords){
      let mouseXY = getMouseXY(e);
      let deltaX = startingCoords.x - mouseXY.x;
      let deltaY = startingCoords.y - mouseXY.y;

      setStartingCoords({
        x: mouseXY.x,
        y: mouseXY.y,
      });

      //dont let the image move outside of the bounds of the viewer
      let newX = currentCard.image.viewerX - deltaX;
      let newY = currentCard.image.viewerY - deltaY;
      newX = (newX > 0) ? 0 : newX;
      newX = (newX < -(currentCard.image.viewerWidth - viewerWidth)) ? -(currentCard.image.viewerWidth - viewerWidth) : newX;
      newY = (newY > 0) ? 0 : newY;
      newY = (newY < -(currentCard.image.viewerHeight - viewerHeight)) ? -(currentCard.image.viewerHeight - viewerHeight) : newY;

      //click to drag/move image
      let tempCard = updateAndReturnCurrentCard("image", {
        ...currentCard.image,
        x: Math.round(newX/magnifyValue),   //need to round cuz react-pdf doesnt like decimals
        y: Math.round(newY/magnifyValue),
        viewerX: newX,
        viewerY: newY,
      });
      setCurrentCard(tempCard);
    }
  }

  // //debug how PDF looks faster with PDFViewer built into browser, dont have to download file each time
  let embeddedPDF = (
    <div className="EmbeddedPDFViewer" style={{
      width: viewerWidth,
      height: viewerHeight,
      outline: 0,
      border: 0,
      display: "inline-block",
      backgroundColor: "rgb(82, 86, 89)",
    }}></div>
  )
  if (!startingCoords){
    embeddedPDF = (
      <div>
        <PDFViewer className="EmbeddedPDFViewer" width={viewerWidth} height={viewerHeight}>
          <PDFDocument cards={props.cards}/>
        </PDFViewer>
      </div>
    )
  }

  return (
    <div>
      <div>
        <CardViewer
          currentCard={currentCard}
          textNoPointerEvent={(startingCoords) ? "nopointerevent" : ""}   //so we can move image while over text, but let text be clickable
          handleMouseDown={handleMouseDown}
          handleMouseUp={handleMouseUp}
          handleMouseMove={handleMouseMove}
          handleInputChange={handleInputChange}
        />
      </div>
      <InputWrapper
        currentCard={currentCard}
        handleInputChange={handleInputChange}
      />
    </div>
  );
}

export default CardEditor;
