import React from 'react';
import { PDFViewer, BlobProvider } from '@react-pdf/renderer';
import { pdfjs, Document } from 'react-pdf'

//custom files
import downloadPDFFile from '../utils/downloadPDFFile.js';
import PDFDocument from './PDFDocument';
import InputWrapper from './InputWrapper';
import * as CardConstants from './CardConstants.js';

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

//wrapper for card editor section (including input + PDF)
//needs to get via props, the currentCard and setter for changing currentCard properties
function CardEditorWrapper(props){
  const currentCard = props.currentCard;
  const setCurrentCard = props.setCurrentCard;

  //PDF document
  const cardDocument = (
    <PDFDocument
      name={currentCard.name}
      japaneseName={currentCard.japaneseName}
      age={currentCard.age}
      job={currentCard.job}
      image={currentCard.image}
    />
  )

  //returns a new card details object with newValue for newProp
  const updateCurrentCard = (newProp, newValue) => {
    return {
      ...currentCard,
      [newProp]: newValue
    }
  }

  //event handlers for inputs
  const handleInputChange = (e) =>{
    switch (e.target.name) {
      case 'image':
        const tempImage = (e.target.files[0]) ? URL.createObjectURL(e.target.files[0]) : CardConstants.defaultImage;
        setCurrentCard(updateCurrentCard("image", tempImage));
        break;
      case 'download':
        downloadPDFFile(cardDocument, currentCard.name + ".pdf");
        break;
      default:
        setCurrentCard(updateCurrentCard(e.target.name, e.target.value));
        break;
    }
  }

  // <PDFViewer scrolling="no" className="PDFViewer">
  //   {cardDocument}
  // </PDFViewer>

  return (
    <div>
      <div className="PDFWrapper" style={{
        height: CardConstants.PDFDimensions.height * 2,
        width: CardConstants.PDFDimensions.width * 2
      }}>
        <BlobProvider document={cardDocument}>
          {({ blob, url, loading, error }) => {
            if (loading || error){
              return <h1>Loading</h1>
            }
            else {
              console.log(url, "wtf");
              return (
                <div>
                  <Document
                    file={url}
                    onLoadSuccess={()=>{console.log("woo")}}
                  >
                  </Document>
                </div>
              )
            }
          }}
        </BlobProvider>
      </div>
      <InputWrapper handleInputChange={handleInputChange}/>
    </div>
  );
}

export default CardEditorWrapper;
