import React from 'react';
import { DebugButtons } from '../utils/DebugTools.js';

//sidebar for editing the deck
function Sidebar(props){
  return (
    <div className="sidebar">
      <div className="sidebarButtonWrapper sidebarContent">
        <button className="noselect button sideBarButton" onClick={props.downloadAllCards}>Download All Cards</button>
        <button className="noselect button sideBarButton" onClick={props.resetAllCards}>Reset All Cards</button>
        <button className="noselect button sideBarButton" onClick={props.addNewCard}>Add New Card</button>
        <DebugButtons
          currentCard={props.currentCard}
          currentIndex={props.currentIndex}
          cards={props.cards}
        />
      </div>
      <div className="cardPreviewWrapper sidebarContent">
        {props.listOfCards}
      </div>
    </div>
  );
}

export default Sidebar;
