import React from 'react';
import { getRandomMember } from './FooterCardConstants.js';

export const MemberCard = (props) => {
  let currentMember = getRandomMember();

  return (
    <div id={props.id} className="memberCard footerCard" style={props.mainStyle}>
      <div className="memberCardText" style={{
        margin:"1.5vh"
      }}>
        <div className="memberCardMainText" style={{
          fontSize: "4.5vh"
        }}>
          {currentMember.name} ({currentMember.age})
        </div>
        <div className="memberCardSubText" style={{
          fontSize: "2vh"
        }}>
          {currentMember.job}&nbsp;<span className="japaneseName">{currentMember.japaneseName}</span>
        </div>
      </div>
      <div className="memberCardShadow"></div>
      <img
        draggable={false}
        className="memberCardImage nopointerevent"
        alt="Background for Card"
        src={currentMember.image.url}
      />
    </div>
  )
}
