//variables for printing in inches
const DPI = 72;   //(dots per inch)
const bleed = 0.125;
let width = 3.5;
let height = 2.5;
const mainFont = 25;
const subFont = 15;

//turn on bleed;
width += bleed;
height += bleed;

//multiply by DPI;
width = width * DPI;
height = height * DPI;
bleed = bleed * DPI;

//how much to magnify the HTML PDF viewer
export const magnifyValue = 2;

//dimensions of the PDF (used to style both PDF and the PDF Wrapper DIV)
//points = inches x dpi (dots per inch)
//double padding for bleed + real padding
export const PDFDimensions = {
  width: width,
  height: height,
  padding: 2 * bleed * DPI,
  mainFont: mainFont,
  subFont: subFont
}

//default card variables
const defaultName = "Candace Green";
const defaultJapaneseName = "キャンディス•グリーン";
const defaultAge = "21";
const defaultJob = "Influencer";

//default image variables
const defaultImage = {
  url: "/images/test.jpg",
  name: "test.jpg",
  width: 1000,
  height: 1400,
  x: (1000/2) + (width * DPI),
  y: (1400/2) + (height * DPI)
};

export function getDefaultCardObject(){
  return {
    name : defaultName,
    japaneseName : defaultJapaneseName,
    age : defaultAge,
    job : defaultJob,
    image : defaultImage,
  }
}
